mapper.select:
SELECT NVL(SUM(AMT),0) AS TITLE
FROM   TOTAL_TRANS
WHERE  USER_ID = #{user_id}
AND    IO_CODE = 'I1'
AND    TO_CHAR(TDATE, 'yyyy-MM') = #{yearMonth}

SELECT 	T.TDATE, T.NUM, T.USER_ID, T.CONTENT, T.AMT, T.IO_CODE, T.CAT_CODE, C.VAL
FROM  	TOTAL_TRANS T
JOIN   	COMMON_CODE C
ON     	T.CAT_CODE  = C.CODE
WHERE  	T.USER_ID = #{user_id}
AND    	T.TDATE = #{tdate}

SELECT 		SUM(AMT) AS AMT, IO_CODE
FROM   		TOTAL_TRANS
WHERE  		USER_ID = #{user_id}
AND    		TDATE = #{tdate}
GROUP BY 	IO_CODE

mapper.insert:

	SELECT 	NVL(MAX(NUM),0) + 1
	FROM 	TOTAL_TRANS
	WHERE   USER_ID = #{user_id}
	AND     TDATE = #{tdate}

INSERT INTO TOTAL_TRANS VALUES
(#{tdate}, #{num}, #{user_id}, #{io_code}, #{content}, #{amt}, #{cat_code})

mapper.update:
UPDATE 	TOTAL_TRANS

	AMT = #{amt},
	CONTENT = #{content},
	IO_CODE = #{io_code},
	CAT_CODE = #{cat_code},

WHERE 	USER_ID = #{user_id}
AND 	TDATE = #{tdate}
AND 	NUM = #{num}

mapper.delete:
DELETE FROM TOTAL_TRANS
WHERE USER_ID = #{user_id}
AND TDATE = #{tdate}
AND NUM = #{num}
