mapper.select:
SELECT 	COUNT(REP_ID)
FROM 	REPORTS
WHERE	REP_ID = #{rep_id}
AND 	REP_CODE = 'SB'
AND		REP_REASON = #{rep_reason}

SELECT 	COUNT(REP_ID)
FROM 	REPORTS
WHERE	REP_ID = #{rep_id}
AND 	REP_CODE = 'SR'
AND		REP_REASON = #{rep_reason}

mapper.delete:
DELETE FROM	REPORTS
WHERE		REP_REASON = #{rep_reason}
AND			REP_CODE = #{rep_code}

	AND	REP_NUM = #{rep_num}
