mapper.insert:
INSERT INTO	SANCTIONS (SANC_NUM, USER_ID, SANC_CODE, SDATE, EDATE, SANC_REASON)
VALUES		( (SELECT NVL(MAX(SANC_NUM), 1) + 1 FROM SANCTIONS)
			 , #{user_id}
			 , #{sanc_code}
			 , TO_CHAR(SYSDATE, 'YYYY-MM-DD')
			 , TO_CHAR(SYSDATE + #{edate}, 'YYYY-MM-DD')
			 ,

				(SELECT CONTENTS FROM BOARDS WHERE BOD_NUM = TO_NUMBER(#{sanc_reason}))					 


			 	(SELECT RE_CONTENTS FROM REPLIES WHERE RE_NUM = TO_NUMBER(#{sanc_reason}))

			)

mapper.update:
UPDATE		USERS
SET 		USER_CODE = 'U2'
WHERE ID =  #{user_id}

UPDATE			BOARDS
SET				BOD_CODE = 'T2'
WHERE BOD_NUM = #{rep_reason}

UPDATE 	SANCTIONS
SET 	EDATE = EDATE + #{edate}
WHERE 	USER_ID = #{user_id}
