mapper.select:
		SELECT	*
		FROM	USERS
		WHERE	1 = 1

			AND		ID = #{id}


       		AND		PW = #{pw}


       		AND		NAME = #{name}


       		AND		EMAIL = #{email}

mapper.update:
UPDATE USERS
PW = #{pw}, EMAIL = #{email}, PHONE = #{phone}, ADDR = #{addr},
WHERE ID = #{id}

mapper.select:
SELECT	COUNT(*)
FROM	USERS
WHERE	USER_SEQ_NUM = #{user_seq_num}

mapper.update:
UPDATE 	USERS
SET 	USER_SEQ_NUM = #{user_seq_num}, ACCESS_TOKEN = #{access_token}, REFRESH_TOKEN = #{refresh_token}
WHERE 	ID = #{id}
